import PropTypes from "prop-types";

import { useRouter } from "next/router";
import Link from "next/link";
import ProgressiveImage from "react-progressive-image";
import moment from "moment";

// import { Box } from "grommet";

import styled from "styled-components";

import { Disc, Hash } from "react-feather";

import styles from "./index.module.scss";

const ArtistName = styled.h4`
  font-size: 1.125rem;
  margin-top: 1rem;
  margin-bottom: 1rem;
  font-weight: 600;
  color: #222;
`;

const cardImg = styled.img`
  transition: all 0.1s ease-in-out;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  border: 1px solid #fff;
  border-radius: 8px;
`;

const StudioCard = ({
  studio: {
    id,
    artist,
    city,
    styles: artStyles,
    dates,
    teaserText,
    imageTeaser,
  },
}) => {
  const router = useRouter();

  const articleLink = {
    pathname: "/studio/[slug]",
    query: { slug: id },
  };

  const openArticle = (id) => {
    router.push(articleLink);
  };

  console.log(dates);

  const teaserSrc = `/img/${artist}/0.jpg`;
  // .replace(/ /g, "")

  return (
    // <div className="article">
    <div className={styles.StudioCard}>
      {/* <Link to={`/studio/${id}`} onClick={() => openArticle(id)}> */}
      <Link href={articleLink}>
        <ProgressiveImage src={teaserSrc} placeholder={`/img/loader.svg`}>
          {(src, loading) => (
            <img
              class={`studioimg-article-${id}`}
              onClick={() => openArticle(id)}
              style={{
                margin: loading ? "33.3%" : "0",
                width: loading ? "33.3%" : "100%",
              }}
              src={src}
              alt={artist}
            />
          )}
        </ProgressiveImage>

        {/* <img src={teaserSrc} /> */}
      </Link>

      <ArtistName>{artist}</ArtistName>

      <h4 className={styles.secondary}>
        <Disc size={18} strokeWidth="2" color="#4b4b4b" fill="#fff" /> {city}
      </h4>

      {dates && (
        <h4 className="secondary">
          Next Visit{" "}
          <strong>
            {moment(dates[0].toDate(), "DD/MM/YYYY hh:mm").format("D MMM")}
          </strong>
        </h4>
      )}

      <p>{teaserText}</p>

      {artStyles && (
        <h4 className="secondary">
          <Hash size={18} strokeWidth="2" color="#4b4b4b" fill="#fff" />{" "}
          {artStyles}
        </h4>
      )}
    </div>
    // </div>
  );
};

StudioCard.propTypes = {
  studio: PropTypes.shape({
    id: PropTypes.string.isRequired,
    artist: PropTypes.string.isRequired,
    // dates: PropTypes.string.isRequired,
    // city: PropTypes.string.isRequired,
    // styles: PropTypes.string.isRequired,
    teaserText: PropTypes.string.isRequired,
    // imageTeaser: PropTypes.string.isRequired,
  }).isRequired,
  // openArticle: PropTypes.func.isRequired,
};

export default StudioCard;
