import { useState, useEffect } from "react";
import PropTypes from "prop-types";

import moment from "moment";
import { X } from "react-feather";

import StudioCard from "../StudioCard";
// import StudioOpen from "../StudioOpen";

import Masonry from "react-masonry-css";
import styles from "./index.module.scss";

import {
  dayButtons,
  dayValues,
  cityValues,
  categoryButtons,
} from "../../config/filters";

import filterArticles from "./filterArticles";

const StudiosRouter = ({ articles }) => {
  const [visibleArticles, setVisibleArticles] = useState(false);
  const [theDay, setTheDay] = useState("All");
  const [theCity, setTheCity] = useState("All");
  const [theCat, setTheCat] = useState("All");

  useEffect(() => {
    const filters = { day: theDay, cat: theCat, city: theCity };
    filterArticles(articles, filters);
    setVisibleArticles(
      filtered.map((article) => {
        return <StudioCard studio={article} key={article.id} />;
      })
    );
    // setTheDay(filter.day);
    // setTheCat(filter.cat);
    // setTheCity(filter.city);
    // sessionStorage.setItem("filters", JSON.stringify(filter));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [articles, theDay, theCat, theCity]);

  return (
    <div className={styles.program} id="the-program">
      <div>
        <div className={styles.filters}>
          <div className={styles.buttons}>
            {dayButtons.map((btn, key) => {
              return (
                <button
                  key={key}
                  className={`${styles.buttonFirst} ${
                    theDay === dayValues[key] ? styles.isChecked : " "
                  }`}
                  onClick={() => setTheDay(dayValues[key])}
                >
                  {btn}
                </button>
              );
            })}
            <br />
            {cityValues.map((btn, key) => {
              return (
                <button
                  key={key}
                  className={`${styles.buttonSecond} ${
                    theCity === btn ? styles.isChecked : " "
                  }`}
                  onClick={() => setTheCity(btn)}
                >
                  {btn}
                </button>
              );
            })}
            <button
              className={styles.buttonSecond}
              onClick={() => setTheCity("All")}
            >
              <X size={16} strokeWidth="3" />
            </button>
            <br />
            {categoryButtons.map((btn, key) => {
              return (
                <button
                  key={key}
                  className={`${styles.buttonSecond} ${
                    theCat === btn ? styles.isChecked : ""
                  }`}
                  onClick={() => setTheCat(btn)}
                >
                  {btn}
                </button>
              );
            })}
            <button
              className={styles.buttonSecond}
              onClick={() => setTheCity("All")}
            >
              <X size={16} strokeWidth="3" />
            </button>
          </div>
        </div>
        <div className="all-articles">
          <Masonry
            breakpointCols={{
              default: 3,
              960: 3,
              768: 2,
              600: 1,
            }}
            className={styles.masonryGrid}
            columnClassName={styles.masonryGridColumn}
          >
            {visibleArticles}
          </Masonry>
        </div>
      </div>
    </div>
  );
};

StudiosRouter.propTypes = {
  articles: PropTypes.array.isRequired,
  // articleIDRoute: PropTypes.string,
};

export default StudiosRouter;
