// import { useState, useEffect } from "react";
// import PropTypes from "prop-types";

import moment from "moment";

// import {
//   dayButtons,
//   dayValues,
//   cityValues,
//   categoryButtons,
// } from "../../config/filters";

const filterStudios = ({ studios, filters }) => {
  let filtered = studios;
  const today = moment();
  const nextWeek = moment().add(7, "days");
  const nextMonth = moment().add(1, "months");
  const nextYear = moment().add(1, "years");

  if (filters.day !== false && filters.day !== "All") {
    filtered = filtered.filter((article) => {
      const datesArray = article.dates.map((d) => d.toDate());
      const dates = datesArray.map((d) => moment(d, "DD/MM/YYYY hh:mm"));

      const isNextWeek = dates.some((d) => d.isBetween(today, nextWeek));
      const isNextMonth = dates.some((d) => d.isBetween(today, nextMonth));
      const isLater = dates.some((d) => d.isBetween(nextMonth, nextYear));

      let result = isNextWeek ? "week " : "";
      result += isNextMonth ? "month " : "";
      result += isLater ? "later" : "";

      return result.includes(filters.day);
    });
  }
  if (filters.cat !== false && filters.cat !== "All") {
    filtered = filtered.filter((article) => {
      const style = filters.cat.toLowerCase();
      return article.styles.includes(style);
    });
  }
  if (filters.city !== false && filters.city !== "All") {
    filtered = filtered.filter((article) => {
      return article.city.includes(filters.city);
    });
  }

  // sessionStorage.setItem("filters", JSON.stringify(filters));

  return filtered;
};

