  const makeParagraphs = (paragraphString, pSeparator) => {
    return paragraphString.split(pSeparator).map((paragraph, index) => (
      <p>{paragraph}</p>
      // <ReactMarkdown key={index}>{paragraph}</ReactMarkdown>
    ));
  };

  // const makeImagesArray = () => {
