import { useState, useEffect } from "react";
import PropTypes from "prop-types";

import { X } from "react-feather";
import Masonry from "react-masonry-css";

import {
  dayButtons,
  dayValues,
  cityValues,
  categoryButtons,
} from "../../config/filters";

import StudioCard from "../StudioCard";
import filterStudios from "./filterStudios";
import styles from "./index.module.scss";

const StudiosFilter = ({ studiosDB: studios }) => {
  const [visibleStudios, setVisibleStudios] = useState(false);
  const [day, setDay] = useState("All");
  const [city, setCity] = useState("All");
  const [medium, setMedium] = useState("All");

  useEffect(() => {
    const filters = { day: day, medium: medium, city: city };
    if (studios) {
      const filtered = filterStudios({ studios, filters });
      setVisibleStudios(
        filtered.map((studio) => {
          return <StudioCard studio={studio} key={studio.id} />;
        })
      );
    }
  }, [studios, day, medium, city]);

  return (
    <div className={styles.program} id="-program">
      <div>
        <div className={styles.filters}>
          <div className={styles.buttons}>
            {dayButtons.map((btn, key) => {
              return (
                <button
                  key={key}
                  className={`${styles.buttonFirst} ${
                    Day === dayValues[key] ? styles.isChecked : " "
                  }`}
                  onClick={() => setDay(dayValues[key])}
                >
                  {btn}
                </button>
              );
            })}
            <br />
            {cityValues.map((btn, key) => {
              return (
                <button
                  key={key}
                  className={`${styles.buttonSecond} ${
                    City === btn ? styles.isChecked : " "
                  }`}
                  onClick={() => setCity(btn)}
                >
                  {btn}
                </button>
              );
            })}
            <button
              className={styles.buttonSecond}
              onClick={() => setCity("All")}
            >
              <X size={16} strokeWidth="3" />
            </button>
            <br />
            {categoryButtons.map((btn, key) => {
              return (
                <button
                  key={key}
                  className={`${styles.buttonSecond} ${
                    Medium === btn ? styles.isChecked : ""
                  }`}
                  onClick={() => setMedium(btn)}
                >
                  {btn}
                </button>
              );
            })}
            <button
              className={styles.buttonSecond}
              onClick={() => setCity("All")}
            >
              <X size={16} strokeWidth="3" />
            </button>
          </div>
        </div>
        <div className="all-studios">
          <Masonry
            breakpointCols={{
              default: 3,
              960: 3,
              768: 2,
              600: 1,
            }}
            className={styles.masonryGrid}
            columnClassName={styles.masonryGridColumn}
          >
            {visibleStudios}
          </Masonry>
        </div>
      </div>
    </div>
  );
};

StudiosFilter.propTypes = {
  studiosDB: PropTypes.array.isRequired,
};

export default StudiosFilter;
