import { useState, useEffect } from "react";
import PropTypes from "prop-types";

import { useRouter } from "next/router";
import Link from "next/link";
import { useHover } from "react-use";

import ProgressiveImage from "react-progressive-image";
import moment from "moment";

import { Disc, Hash } from "react-feather";

import styles from "./index.module.scss";

const StudioCard = ({
  studio: { id, artist, city, styles: artStyles, dates, textMini },
}) => {
  const router = useRouter();

  const articleLink = {
    pathname: "/studio/[slug]",
    query: { slug: id },
  };

  const openArticle = (id) => {
    router.push(articleLink);
  };

  const [hoverImg, setHoverImg] = useState("0");
  const teaserSrc = `/img/${artist}/${hoverImg}.jpg`;

  const element = (hovered) => (
    <ProgressiveImage src={teaserSrc} placeholder={`/img/loader.svg`}>
      {(src, loading) => (
        <img
          className={styles.cardImg}
          onClick={() => openArticle(id)}
          src={src}
          alt={artist}
        />
      )}
    </ProgressiveImage>
  );
  const [burnsGallery, hovered] = useHover(element);

  useEffect(() => {
    if (hovered) {
      setHoverImg(hoverImg + (1 % 3));
    }
  }, [hovered]);

  return (
    <div className={styles.StudioCard}>
      <div className={styles.imgContainer}>
        <Link href={articleLink}>{burnsGallery}</Link>
      </div>

      <h4 className={styles.primary}>{artist}</h4>

      <h4 className={styles.secondary}>
        <Disc
          className={styles.Icon}
          size={18}
          strokeWidth="2"
          color="#4b4b4b"
          fill="#fff"
        />{" "}
        {city}
      </h4>

      {dates && (
        <h4 className={styles.secondary}>
          Next Visit{" "}
          <strong>
            {moment(dates[0].toDate(), "DD/MM/YYYY hh:mm").format("D MMM")}
          </strong>
        </h4>
      )}

      <p className={styles.textMini}>{textMini}</p>

      {artStyles && (
        <h4 className={styles.secondary}>
          <Hash
            className={styles.Icon}
            size={18}
            strokeWidth="2"
            color="#4b4b4b"
            fill="#fff"
          />{" "}
          {artStyles}
        </h4>
      )}
    </div>
  );
};

StudioCard.propTypes = {
  studio: PropTypes.shape({
    id: PropTypes.string.isRequired,
    artist: PropTypes.string.isRequired,
    dates: PropTypes.array.isRequired,
    city: PropTypes.string.isRequired,
    styles: PropTypes.string.isRequired,
    textMini: PropTypes.string.isRequired,
  }).isRequired,
};

export default StudioCard;
