import { useContext, useState, useEffect } from "react";
import "firebase/firestore";
import "firebase/functions";
import { useCollection } from "react-firebase-hooks/firestore";

import { FirebaseContext } from "../../services/firebase.js";

import { Grid, Row, Col } from "react-flexbox-grid/dist/react-flexbox-grid";
import Link from "next/link";

import ProgressiveImage from "react-progressive-image";
import Image from "next/image";

import styles from "./index.module.scss";

import { Box, Heading, Text } from "grommet";

const Studios = () => {
  const margin = "medium";
  const sectionMargin = { vertical: "12rem" };

  const firebase = useContext(FirebaseContext);
  console.log(FirebaseContext);

  const [value, loading, error] = useCollection(
    firebase.firestore().collection("studios"),
    {
      snapshotListenOptions: { includeMetadataChanges: true },
    }
  );
  //   const sayHello = firebase.functions().httpsCallable("sayHelloFunc");

  //   const getProducts = firebase.functions().httpsCallable("getProductsCF");

  const [resultfromCF, setResultfromCF] = useState([]);

  useEffect(() => {
    // sayHello({ name: "Shaun" }).then((result) => {
    //   console.log(result.data);
    // });
    // getProducts({}).then((result) => {
    //   console.log(result.data);
    //   setResultfromCF(result.data);
    // });
  }, []);

  return (
    <Grid fluid align="center">
      <section>
        <Row id={styles.studio}>
          <Col xs={12} md={12}>
            <p>
              {error && <strong>Error: {JSON.stringify(error)}</strong>}
              {loading && <span>Collection: Loading...</span>}
              {value && (
                <span>
                  Collection:{" "}
                  {value.docs.map((doc) => (
                    <React.Fragment key={doc.id}>
                      {JSON.stringify(doc.data())},{" "}
                    </React.Fragment>
                  ))}
                </span>
              )}
            </p>

            <Image
              src={`/img/intro/banner-0.jpg`}
              alt="banner"
              layout="responsive"
              width="100%"
              height="62.23%"
            />
          </Col>
          <Col xs={12} md={12}>
            <Box margin={{ vertical: "6rem" }} align="center">
              <Heading level={2} margin="xlarge" className="first">
                expand the art space.
              </Heading>
            </Box>
          </Col>
        </Row>
      </section>
    </Grid>
  );
};
export default Studios;
