import { useState, useEffect } from "react";
import Router from "next/router";
import Link from "next/link";

import firebase from "firebase/app";
import "firebase/auth";

import initFirebase from "services/firebase";

import { Grid, Row, Col } from "react-flexbox-grid/dist/react-flexbox-grid";
import { Box, Heading, Text } from "grommet";

import styles from "./index.module.scss";

initFirebase();

const SignIn = () => {
  const [authorizing, setAuthorizing] = useState(false);

  const [isNewUser, setIsNewUser] = useState(isSignUp);
  const [email, emailInput] = useInput({ type: "text", placeholder: "Email" });
  const [password, passwordInput] = useInput({
    type: "password",
    placeholder: "Pass",
  });

  const [displayName, displayNameInput] = useInput({
    type: "text",
    placeholder: "Your Name",
  });

  const margin = "medium";
  const sectionMargin = { vertical: "12rem" };

  return (
    <Grid fluid id={styles.signin} align="center">
      <section>
        <Box margin={sectionMargin} align="center">
          <Row>
            <Heading level={2} margin={{ vertical: "1rem" }}>
              Ready to give it a try?
            </Heading>
          </Row>
          <Row>
            <Col xs={12} md={6}>
              <Box
                align="center"
                pad={{ vertical: "small" }}
                margin={{ vertical: "xlarge" }}
              >
                <button className="button">
                  <Link href="/studios">Explore Artists</Link>
                </button>

                <Text size="medium" margin="medium">
                  Discover artists by location or medium and plan a visit to
                  their studios
                </Text>
              </Box>
            </Col>

            <Col xs={12} md={6}>
              <Box
                align="center"
                pad={{ vertical: "small" }}
                margin={{ vertical: "xlarge" }}
              ></Box>
            </Col>
          </Row>
        </Box>
      </section>
    </Grid>
  );
};

export default SignIn;
