import { useState, useEffect, useRef } from "react";
import PropTypes from "prop-types";

import { useRouter } from "next/router";
import Link from "next/link";
// import { useHover } from "react-use";

import ProgressiveImage from "react-progressive-image";
import moment from "moment";

import { Disc, Hash } from "react-feather";

import styles from "./index.module.scss";

function useHover() {
  const [value, setValue] = useState(false);

  const ref = useRef(null);

  const handleMouseOver = () => setValue(true);
  const handleMouseOut = () => setValue(false);

  useEffect(
    () => {
      const node = ref.current;
      if (ref && node) {
        node.addEventListener("mouseover", handleMouseOver);
        node.addEventListener("mouseout", handleMouseOut);

        return () => {
          node.removeEventListener("mouseover", handleMouseOver);
          node.removeEventListener("mouseout", handleMouseOut);
        };
      }
    },
    [ref.current] // Recall only if ref changes
  );

  return [ref, value];
}

const StudioCard = ({
  studio: { id, artist, city, styles: artStyles, dates, textMini },
}) => {
  const router = useRouter();

  const articleLink = {
    pathname: "/studio/[slug]",
    query: { slug: id },
  };

  const openArticle = (id) => {
    router.push(articleLink);
  };

  const [hoverImg, setHoverImg] = useState("0");
  const [hoveredImg, setHoveredImg] = useState(false);

  const teaserSrc = `/img/${artist}/${hoverImg}.jpg`;

  const burnsGallery = () => (
    <ProgressiveImage
      ref={hoverRef}
      src={teaserSrc}
      placeholder={`/img/loader.svg`}
    >
      {(src, loading) => (
        <img
          className={styles.cardImg}
          onClick={() => openArticle(id)}
          src={src}
          alt={artist}
        />
      )}
    </ProgressiveImage>
  );

  // const [hoverRef, isHovered] = useHover();

  // const [burnsGallery, hovered] = useHover(element);

  function sleep(ms) {
    return new Promise((resolve) => setTimeout(resolve, ms));
  }

  useEffect(() => {
    console.log(hoveredImg);

    // if (isHovered) {
    //   sleep(2000).then(() => {
    //     setHoverImg((hoverImg + 1) % 3);
    //     console.log(hoverImg);
    //     console.log(teaserSrc);
    //   });
    // }
  }, [hoveredImg]);

  return (
    <div className={styles.StudioCard}>
      <div
        className={styles.imgContainer}
        onmouseenter={setHoveredImg(true)}
        onmouseleave={setHoveredImg(false)}
      >
        <Link href={articleLink}>
          <ProgressiveImage
            // ref={hoverRef}
            src={teaserSrc}
            placeholder={`/img/loader.svg`}
          >
            {(src, loading) => (
              <img
                className={styles.cardImg}
                onClick={() => openArticle(id)}
                src={src}
                alt={artist}
              />
            )}
          </ProgressiveImage>
        </Link>
      </div>

      <h4 className={styles.primary}>{artist}</h4>

      <h4 className={styles.secondary}>
        <Disc
          className={styles.Icon}
          size={18}
          strokeWidth="2"
          color="#4b4b4b"
          fill="#fff"
        />{" "}
        {city}
      </h4>

      {dates && (
        <h4 className={styles.secondary}>
          Next Visit{" "}
          <strong>
            {moment(dates[0].toDate(), "DD/MM/YYYY hh:mm").format("D MMM")}
          </strong>
        </h4>
      )}

      <p className={styles.textMini}>{textMini}</p>

      {artStyles && (
        <h4 className={styles.secondary}>
          <Hash
            className={styles.Icon}
            size={18}
            strokeWidth="2"
            color="#4b4b4b"
            fill="#fff"
          />{" "}
          {artStyles}
        </h4>
      )}
    </div>
  );
};

StudioCard.propTypes = {
  studio: PropTypes.shape({
    id: PropTypes.string.isRequired,
    artist: PropTypes.string.isRequired,
    dates: PropTypes.array.isRequired,
    city: PropTypes.string.isRequired,
    styles: PropTypes.string.isRequired,
    textMini: PropTypes.string.isRequired,
  }).isRequired,
};

export default StudioCard;
